
import * as R from 'ramda'
import request from 'superagent'


import items from 'api/mockItems'
import categories from 'api/mockCategories'


export const fetchItems = async ()=>{
  const {body} = await request.get(
    'http://www.mocky.io/v2/5d6669983300004d00449c7e'
  )
  return body.items


 /* return new Promise((resolve, reject)=>{
    resolve(items)
  })*/
}

export const loadMoreItems =async ({offset})=>{
  //fetch.get('http://google.com/api/items?offset=${offset}')
  return new Promise((resolve, reject)=>{
    resolve(items)
  })
}

export const fetchItemById = async (id)=>{
  return new Promise((resolve, reject)=>{
    const item = R.find(R.propEq('id', id), items)
    resolve(item)
  })
}


export const fetchCategories = async () => {
  return new Promise((resolve, reject) => {
    resolve(categories)
  })
}